import React, { useState } from "react";
import axios from "axios";
import "./header.css";

function getResult(feedingOption, keyword, selectedFile) {}
function Content() {
  const [step, setStep] = useState(1);
  const [feedingOption, setFeedingOption] = useState(1);
  const [isRunDetector, setIsRunDetector] = useState(true);
  const [keyword, setKeyword] = useState("");
  const [max_tweets, setMaxTweets] = useState(5);
  const [dataset, setDataset] = useState([]);
  const [newstext, setNewsText] = useState("");

  const [selectedFile, setSelectedFile] = useState();
  const [isFilePicked, setIsFilePicked] = useState(false);
  const [result, setResult] = useState([]);

  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const runFNDetector = () => {
    setIsRunDetector(true);
    if(feedingOption === 1){
      axios
      .get(
        `http://207.254.64.136:5000/fetchtweets?keyword=${keyword}&max_tweets=10`
      )
      .then((e) => {
        axios.get(`http://207.254.64.136:5000/predict`).then((e) => {
          //setIsRunDetector(true);
          console.log(e);
          setResult(e.data);
          const obj = e.data.data.text;
          const { labels1, labels2, labels3 } = e.data;
          const txt = Object.keys(obj).map((key) => {
            return {
              text: obj[key],
              rslt1: labels1[key],
              rslt2: labels2[key],
              rslt3: labels3[key],
            };
          });
          setDataset(txt);
        });
      });
    }

    if(feedingOption === 2){
      var bodyFormData = new FormData();
      bodyFormData.append("file", selectedFile)
      axios({
        method: "post",
        url: `http://207.254.64.136:5000/fileupload`,
        data: bodyFormData,
        headers: { "Content-Type": "multipart/form-data" },
      }).then((e) => {
          //setIsRunDetector(true);
          console.log(e);
          setResult(e.data);
          const obj = e.data.data.text;
          const { labels1, labels2, labels3 } = e.data;
          const txt = Object.keys(obj).map((key) => {
            return {
              text: obj[key],
              rslt1: labels1[key],
              rslt2: labels2[key],
              rslt3: labels3[key],
            };
          });
          setDataset(txt);
        });
    }

    if(feedingOption === 3){
      var bodyFormData = new FormData();
      bodyFormData.append("newstext", newstext)
      axios({
        method: "post",
        url: `http://207.254.64.136:5000/predictwithatext`,
        data: bodyFormData,
        headers: { "Content-Type": "multipart/form-data" },
      }).then((e) => {
          //setIsRunDetector(true);
          console.log(e);
          setResult(e.data);
          const obj = e.data.data.text;
          const { labels1, labels2, labels3 } = e.data;
          const txt = Object.keys(obj).map((key) => {
            return {
              text: obj[key],
              rslt1: labels1[key],
              rslt2: labels2[key],
              rslt3: labels3[key],
            };
          });
          setDataset(txt);
        });
    }
  };

  return (
    <React.Fragment>

      <div className="container" id="daterangepicker-container">
        <div className="row">
          <div className="col-lg-12">

          </div>
        </div>
        {isRunDetector ? (
          feedingOption === 1 ? (
            <div className="row m-t-1 m-b-2">
              <div className="col-lg-9">
                <div className="block social-block social-twitter">
                  <div
                    className="panel panel-default b-a-0"
                    style={{
                      "-webkit-box-shadow": "0px 4px 20px 3px rgba(0,0,0,0.8);",
                      "-moz-box-shadow": "0px 4px 20px 3px rgba(0,0,0,0.11);",
                      boxShadow: "0px 4px 20px 3px rgba(0,0,0,0.11);",
                    }}
                  >
                    <div className="panel-body">
                      <table className="table table-striped" id="table_id">
                        <thead>
                          <tr>
                            <th className="small text-muted text-uppercase">
                              <strong>#</strong>
                            </th>
                            <th className="small text-muted text-uppercase">
                              <strong>Text</strong>
                            </th>

                            <th className="small text-muted text-right text-uppercase">
                              <strong>Logistic Regression</strong>
                            </th>
                            <th className="small text-muted text-right text-uppercase">
                              <strong>Decision Tree</strong>
                            </th>
                            <th className="small text-muted text-right text-uppercase">
                              <strong>Random Forest</strong>
                            </th>
                          </tr>
                        </thead>
                        <tbody>
                          {dataset.map((r, idx) => (
                            <tr key={idx}>
                              <td className="v-a-m">{idx + 1}</td>
                              <td className="v-a-m">
                                {" "}
                                <span>{r.text}</span>
                              </td>
                              <td className="text-right v-a-m">
                                <span
                                  className={
                                    r.rslt1 === "fake"
                                      ? "badge badge-outline badge-warning  text-uppercase"
                                      : "badge badge-outline badge-success text-uppercase"
                                  }
                                >
                                  <span>{r.rslt1}</span>
                                </span>
                              </td>
                              <td className="text-right v-a-m">
                                <span
                                  className={
                                    r.rslt2 === "fake"
                                      ? "badge badge-outline badge-warning  text-uppercase"
                                      : "badge badge-outline badge-success text-uppercase"
                                  }
                                >
                                  <span>{r.rslt2}</span>
                                </span>
                              </td>
                              <td className="text-right v-a-m">
                                <span
                                  className={
                                    r.rslt3 === "fake"
                                      ? "badge badge-outline badge-warning  text-uppercase"
                                      : "badge badge-outline badge-success text-uppercase"
                                  }
                                >
                                  <span>{r.rslt3}</span>
                                </span>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-lg-3">
                <div className="block social-block social-twitter">
                  <div
                    className="panel panel-default b-a-0"
                    style={{
                      "-webkit-box-shadow": "0px 4px 20px 3px rgba(0,0,0,0.8);",
                    }}
                  >
                    <div className="panel-heading">Total Tweets</div>
                    <div className="panel-body">
                      <h2 className="f-w-300 m-t-0">{dataset.length}</h2>
                    </div>
                  </div>
                </div>
                <div
                  className="panel panel-default b-a-0"
                  style={{
                    "-webkit-box-shadow": "0px 4px 20px 3px rgba(0,0,0,0.8);",
                  }}
                >
                  <div className="panel-heading">Fake</div>
                  <div className="panel-body">
                    <h2 className="f-w-300 m-t-0">
                      {
                        dataset.filter(
                          (d) =>
                            d.rslt1 === "fake" &&
                            d.rslt2 === "fake" &&
                            d.rslt3 === "fake"
                        ).length
                      }
                    </h2>
                  </div>
                </div>
              </div>
              <div
                className="panel panel-default b-a-0"
                style={{
                  "-webkit-box-shadow": "0px 4px 20px 3px rgba(0,0,0,0.8);",
                }}
              >
                <div className="panel-heading">Real</div>
                <div className="panel-body">
                  <h2 className="f-w-300 m-t-0">
                    {
                      dataset.filter(
                        (d) =>
                          !(
                            d.rslt1 === "fake" &&
                            d.rslt2 === "fake" &&
                            d.rslt3 === "fake"
                          )
                      ).length
                    }
                  </h2>
                </div>
              </div>
            </div>
          ) : feedingOption === 3 ? (
            <div className="row m-t-1 m-b-2">
              <div className="col-lg-12">
                <div className="block social-block social-twitter">
                  <div
                    className="panel panel-default b-a-0"
                    style={{
                      "-webkit-box-shadow": "0px 4px 20px 3px rgba(0,0,0,0.8);",
                      "-moz-box-shadow": "0px 4px 20px 3px rgba(0,0,0,0.11);",
                      boxShadow: "0px 4px 20px 3px rgba(0,0,0,0.11);",
                    }}
                  >
                    <div className="panel-body">
                      <table className="table table-striped">
                        <thead>
                          <tr>
                            <th className="small text-muted text-uppercase">
                              <strong>#</strong>
                            </th>
                            <th className="small text-muted text-uppercase">
                              <strong>Text</strong>
                            </th>

                            <th className="small text-muted text-right text-uppercase">
                              <strong>Logistic Regression</strong>
                            </th>
                            <th className="small text-muted text-right text-uppercase">
                              <strong>Decision Tree</strong>
                            </th>
                            <th className="small text-muted text-right text-uppercase">
                              <strong>Random Forest</strong>
                            </th>
                          </tr>
                        </thead>
                        <tbody>
                          {dataset.map((r, idx) => (
                            <tr key={idx}>
                              <td className="v-a-m">{idx + 1}</td>
                              <td className="v-a-m">
                                {" "}
                                <span>{r.text}</span>
                              </td>
                              <td className="text-right v-a-m">
                                <span
                                  className={
                                    r.rslt1 === "fake"
                                      ? "badge badge-outline badge-warning  text-uppercase"
                                      : "badge badge-outline badge-success text-uppercase"
                                  }
                                >
                                  <span>{r.rslt1}</span>
                                </span>
                              </td>
                              <td className="text-right v-a-m">
                                <span
                                  className={
                                    r.rslt2 === "fake"
                                      ? "badge badge-outline badge-warning  text-uppercase"
                                      : "badge badge-outline badge-success text-uppercase"
                                  }
                                >
                                  <span>{r.rslt2}</span>
                                </span>
                              </td>
                              <td className="text-right v-a-m">
                                <span
                                  className={
                                    r.rslt3 === "fake"
                                      ? "badge badge-outline badge-warning  text-uppercase"
                                      : "badge badge-outline badge-success text-uppercase"
                                  }
                                >
                                  <span>{r.rslt3}</span>
                                </span>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
              </div>

            </div>
          ) : feedingOption === 2 ? (
            <div className="row m-t-1 m-b-2">
              <div className="col-lg-12">
                <div className="block social-block social-twitter">
                  <div
                    className="panel panel-default b-a-0"
                    style={{
                      "-webkit-box-shadow": "0px 4px 20px 3px rgba(0,0,0,0.8);",
                      "-moz-box-shadow": "0px 4px 20px 3px rgba(0,0,0,0.11);",
                      boxShadow: "0px 4px 20px 3px rgba(0,0,0,0.11);",
                    }}
                  >
                    <div className="panel-body">
                      <table className="table table-striped">
                        <thead>
                          <tr>
                            <th className="small text-muted text-uppercase">
                              <strong>#</strong>
                            </th>
                            <th className="small text-muted text-uppercase">
                              <strong>Text</strong>
                            </th>

                            <th className="small text-muted text-right text-uppercase">
                              <strong>Logistic Regression</strong>
                            </th>
                            <th className="small text-muted text-right text-uppercase">
                              <strong>Decision Tree</strong>
                            </th>
                            <th className="small text-muted text-right text-uppercase">
                              <strong>Random Forest</strong>
                            </th>
                          </tr>
                        </thead>
                        <tbody>
                          {dataset.map((r, idx) => (
                            <tr key={idx}>
                              <td className="v-a-m">{idx + 1}</td>
                              <td className="v-a-m">
                                {" "}
                                <span>{r.text}</span>
                              </td>
                              <td className="text-right v-a-m">
                                <span
                                  className={
                                    r.rslt1 === "fake"
                                      ? "badge badge-outline badge-warning  text-uppercase"
                                      : "badge badge-outline badge-success text-uppercase"
                                  }
                                >
                                  <span>{r.rslt1}</span>
                                </span>
                              </td>
                              <td className="text-right v-a-m">
                                <span
                                  className={
                                    r.rslt2 === "fake"
                                      ? "badge badge-outline badge-warning  text-uppercase"
                                      : "badge badge-outline badge-success text-uppercase"
                                  }
                                >
                                  <span>{r.rslt2}</span>
                                </span>
                              </td>
                              <td className="text-right v-a-m">
                                <span
                                  className={
                                    r.rslt3 === "fake"
                                      ? "badge badge-outline badge-warning  text-uppercase"
                                      : "badge badge-outline badge-success text-uppercase"
                                  }
                                >
                                  <span>{r.rslt3}</span>
                                </span>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
              </div>

            </div>
          ) : null
        ) : null}
      </div>
    </React.Fragment>
  );
}

export default Content;
