          setResult(e.data);
          const obj = e.data.data.text;
          const { labels1, labels2, labels3 } = e.data;
          const txt = Object.keys(obj).map((key) => {
            return {
              text: obj[key],
              rslt1: labels1[key],
              rslt2: labels2[key],
              rslt3: labels3[key],
            };
          });
          setDataset(txt);
        });
    }
  };

