import React, { useState } from "react";
import axios from "axios";
import "./header.css";

function getResult(feedingOption, keyword, selectedFile) {}
function Content() {
  const [step, setStep] = useState(1);
  const [feedingOption, setFeedingOption] = useState(1);
  const [isRunDetector, setIsRunDetector] = useState(true);
  const [keyword, setKeyword] = useState("");
  const [max_tweets, setMaxTweets] = useState(5);
  const [dataset, setDataset] = useState([]);
  const [newstext, setNewsText] = useState("");

  const [selectedFile, setSelectedFile] = useState();
  const [isFilePicked, setIsFilePicked] = useState(false);
  const [result, setResult] = useState([]);

  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const runFNDetector = () => {
    setIsRunDetector(true);
    if(feedingOption === 1){
      axios
      .get(
        `http://207.254.64.136:5000/fetchtweets?keyword=${keyword}&max_tweets=10`
      )
      .then((e) => {
        axios.get(`http://207.254.64.136:5000/predict`).then((e) => {
          //setIsRunDetector(true);
          console.log(e);
          setResult(e.data);
          const obj = e.data.data.text;
          const { labels1, labels2, labels3 } = e.data;
          const txt = Object.keys(obj).map((key) => {
            return {
              text: obj[key],
              rslt1: labels1[key],
              rslt2: labels2[key],
              rslt3: labels3[key],
            };
          });
          setDataset(txt);
        });
      });
    }

    if(feedingOption === 2){
      var bodyFormData = new FormData();
      bodyFormData.append("file", selectedFile)
      axios({
        method: "post",
        url: `http://207.254.64.136:5000/fileupload`,
        data: bodyFormData,
        headers: { "Content-Type": "multipart/form-data" },
      }).then((e) => {
          //setIsRunDetector(true);
          console.log(e);
          setResult(e.data);
          const obj = e.data.data.text;
          const { labels1, labels2, labels3 } = e.data;
          const txt = Object.keys(obj).map((key) => {
            return {
              text: obj[key],
              rslt1: labels1[key],
              rslt2: labels2[key],
              rslt3: labels3[key],
            };
          });
          setDataset(txt);
        });
    }

    if(feedingOption === 3){
      var bodyFormData = new FormData();
      bodyFormData.append("newstext", newstext)
      axios({
        method: "post",
        url: `http://207.254.64.136:5000/predictwithatext`,
        data: bodyFormData,
        headers: { "Content-Type": "multipart/form-data" },
      }).then((e) => {
          //setIsRunDetector(true);
          console.log(e);
          setResult(e.data);
          const obj = e.data.data.text;
          const { labels1, labels2, labels3 } = e.data;
          const txt = Object.keys(obj).map((key) => {
            return {
              text: obj[key],
              rslt1: labels1[key],
              rslt2: labels2[key],
              rslt3: labels3[key],
            };
          });
          setDataset(txt);
        });
    }
  };

  return (
    <React.Fragment>
      <div className="sub-navbar sub-navbar__header-breadcrumbs">
        <div className="container">
          <div className="row">
            <div className="col-lg-12 sub-navbar-column">
              <div className="sub-navbar-header">
                <h3>Fake News Detector</h3>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="container" id="daterangepicker-container">
        <div className="row">
          <div className="col-lg-12">
            <div className="panel panel-default shadow-box b-a-0">
              <div className="panel-body">
                <form
                  className="form-inline"
                  name="form1"
                  id="form1"
                  onsubmit="return false;"
                >
                  <div className="col-lg-5">
                    {step === 1 ? (
                      <div className="feeding-steps" id="feeding-step-1">
                        <h5>Select Feeding Options</h5>
                        <div className="radio">
                          <label>
                            <input
                              type="radio"
                              name="feedingOption"
                              value="1"
                              checked={feedingOption === 1}
                              onClick={(e) => setFeedingOption(1)}
                            />{" "}
                            1. Real Time Tweet
                          </label>
                        </div>
                        <br />
                        {/* <div className="radio">
                          <label>
                            <input
                              type="radio"
                              name="feedingOption"
                              value="4"
                              checked={feedingOption === 4}
                              onClick={(e) => setFeedingOption(4)}
                            />{" "}
                            2. News Feed (max: 5)
                          </label>
                        </div>
                        <br /> */}
                        <div className="radio">
                          <label>
                            <input
